package Com.Syntax.Class02;

public class ArithemticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1, num2;
		
		num1=10;
		num2=12;
		
		System.out.println(num1+num2);
		System.out.println(num2-num1);
		
		double num3=10.99;
		double num4=2.99;
		
		double mult=num3*num4;
		
		System.out.println(mult);
		
		double div=num3/num4;
		System.out.println("then result of division of 2 double values= "+div);
		
		float f=10.99f;
		float f1=2.99f;
		
		System.out.println("the result of division of 2 float values = "+f/f1);
		
		
		
		
		
		

	}

}
