package Com.Syntax.Class02;

public class VariableDeclaration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1 way to create a variable
		
		//create Variable= assigning value 
		// declare a variable=initializing 
		
		double $num=12.99;
		System.out.println($num);
		
		
		//2 way to create a variable:
		
		   //declare a variable 
		   //initialize or assigning value 
		
		int mybox; //we declare variable only once 
	    mybox=135;// assigning  value 
	
		System.out.println(mybox);//135
		
		
		
		mybox=155;//reasigning value
		System.out.println(mybox);//155
		
		
		mybox=2000;
		
		System.out.println(mybox);
		// 3 way 
		
		// if cariable is of same type, we can declare in one line 
		
		byte  num1,num2,num3;
		num1=10;
		num2=20;
		num3=30;
		
		System.out.println(num1);
		System.out.println(num2);
		
		
		
		System.out.println(num3);
		
		
		// what will be the output?
		
		char char1='C';
		char char2=	'd';
		
		char char3=char1;
		
		System.out.println(char1);
		System.out.println(char2);
		
		System.out.println(char3);
		
		
	}
	
	

}
